package week1.day1.Assigments;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int Fact=1;
		for(int i=5;i>=1;i--) {
			
			Fact=Fact * i;
			System.out.print(i +"*");
			
					}
		System.out.println("=" +Fact);

	}
	


}
